import React, { FunctionComponent, useState, useEffect } from 'react';
import Pokemon from "./models/pokemon"
import POKEMONS from './models/mock-pokemon'

const App: FunctionComponent = () => {
 const [pokemons, setPokemons] = useState<Pokemon[]>(POKEMONS);

 useEffect(() => {
    setPokemons(pokemons)
 },[])

 return (

    <div>
        <h1>Bievenue sur votre pokedex vous avez {pokemons.length} pokemons </h1>
        <ul>
            {pokemons.map(({name, picture})=> (

                <div className="col s3 m7">
                  <div className="card small">
                    <div className="card-image">
                      <img src={picture} />
                      <span className="card-title" key={name}> {name}Lortenzo</span>
                    </div>
                    <div className="card-content">
                      <p>I am a very simple card. I am good at containing small bits of information.
                      I am convenient because I require little markup to use effectively.</p>
                    </div>
                    <div className="card-action">
                      <a href="#">This is a link</a>
                    </div>
                  </div>
                </div>

            ))}
        </ul>
    </div>

 ) 
}

export default App;
